import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  getAllCountries,
  orderAlphabetical,
  postTouristActivy,
} from "../redux/actions";

const Crearactividadturist = () => {
  const dispatch = useDispatch();
  const paises = useSelector((state) => state.countries);
  const [input, setInput] = useState({
    name: "",
    dificult: "",
    duration: "",
    season: "",
  });
  useEffect(() => {
    dispatch(getAllCountries());
  }, []);

  function tomarValores(e) {
    e.preventDefault();

    setInput({ ...input, [e.target.name]: e.target.value });
  }

  function postear(e) {
    e.preventDefault();
    dispatch(postTouristActivy(input));
  }

  return (
    <div>
      <Link to="/Home">
        <button>Volver</button>
      </Link>
      <h1>Crear una actividad turistica</h1>
      <div>
        <form onSubmit={postear}>
          <label>Nombre</label>
          <input
            type="text"
            value={input.name}
            name="name"
            onChange={tomarValores}
          />
          <label>Dificultad</label>
          <input
            type="number"
            required={true}
            min={1}
            max={5}
            value={input.dificult}
            name="dificult"
            onChange={tomarValores}
          />
          <label>Duracion</label>
          <input
            type="number"
            value={input.duration}
            name="duration"
            onChange={tomarValores}
          />
          <label>Temporada</label>
          <input
            type="text"
            value={input.season}
            name="season"
            onChange={tomarValores}
          />
          <label>Paises</label>
          <select onClick={dispatch(orderAlphabetical("asc"))}>
            {paises.map((e) => (
              <option key={e} value={e.name}>
                {e.name}
              </option>
            ))}
          </select>
          <button type="submit">enviar</button>
        </form>
      </div>
    </div>
  );
};

export default Crearactividadturist;
