import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getAllCountries, orderAlphabetical } from "../redux/actions";

const Crearactividadturist = () => {
  const dispatch = useDispatch();
  const paises = useSelector((state) => state.countries);
  const [input, setInput] = useState({
    name: "",
    dificult: "",
    duration: "",
    season: "",
  });
  useEffect(() => {
    dispatch(getAllCountries());
  }, []);

  return (
    <div>
      <Link to="/Home">
        <button>Volver</button>
      </Link>
      <h1>Crear una actividad turistica</h1>
      <div>
        <form typeof="submit">
          <label>Nombre</label>
          <input type="text" value={input.name} name="name" />
          <label>Dificultad</label>
          <input type="number" value={input.dificult} name="dificult" />
          <label>Duracion</label>
          <input type="number" value={input.duration} name="duration" />
          <label>Temporada</label>
          <input type="text" value={input.season} name="season" />
          <label>Paises</label>
          <select onClick={dispatch(orderAlphabetical("asc"))}>
            {paises.map((e) => (
              <option value={e.name}>{e.name}</option>
            ))}
          </select>
        </form>
      </div>
    </div>
  );
};

export default Crearactividadturist;
