import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  filterByContinent,
  getAllCountries,
  orderAlphabetical,
} from "../redux/actions";
import style from "../styles/paises.module.css";
import Buscador from "./Buscador";
import Paginado from "./Paginado";
import Pais from "./Pais";

const Paises = () => {
  const dispatch = useDispatch();
  var pais = useSelector((state) => state.countries);
  const pais2 = useSelector((state) => state.filtrado);
  const [alfa, setAlfa] = useState("s");
  const [filt, setFilt] = useState("Todos");
  const [paginaActual, setPaginaActual] = useState(1);
  const [paisesPorPag, setPaisesPorPag] = useState(10);
  const [orden, setOrden] = useState("");
  const ultimoPaisDeLaPag = paginaActual * paisesPorPag;
  const primerPaisDeLaPag = ultimoPaisDeLaPag - paisesPorPag;
  const paisesPagActual = pais.slice(primerPaisDeLaPag, ultimoPaisDeLaPag);

  const paginado = (numero) => {
    setPaginaActual(numero);
  };

  useEffect(() => {
    dispatch(getAllCountries());
    console.log("cambio en el estado del componente");
  }, []);

  const filtroContinente = (e) => {
    e.preventDefault();
    if (alfa !== "s") {
      dispatch(filterByContinent(e.target.value));

      setPaginaActual(1);
    }
    dispatch(filterByContinent("Todos"));
    dispatch(filterByContinent(e.target.value));

    setPaginaActual(1);
  };
  const ordenAlfabetico = (e) => {
    e.preventDefault();
    if (alfa === "s") {
      pais = pais2;
      dispatch(filterByContinent(filt));
      setPaginaActual(1);
    }

    dispatch(orderAlphabetical(e.target.value));
    setPaginaActual(1);
    setOrden(`Ordenado ${e.target.value}`);
  };

  function filtrar(filt) {
    if (filt === "menos") {
      const f = pais.sort(function (a, b) {
        let c = Number(a.poblacion.replace(/[.]/g, ""));
        let d = Number(b.poblacion.replace(/[.]/g, ""));

        if (c === d) {
          return 0;
        }
        if (c < d) {
          return -1;
        }
        return 1;
      });
      pais = f;
    }

    if (filt === "mas") {
      const w = pais.sort(function (a, b) {
        let c = Number(a.poblacion.replace(/[.]/g, ""));
        let d = Number(b.poblacion.replace(/[.]/g, ""));

        if (c === d) {
          return 0;
        }
        if (c > d) {
          return -1;
        }
        return 1;
      });
      pais = w;
    }

    dispatch(filterByContinent("Todos"));
  }

  return (
    <div>
      <button onClick={filtrar}>prueba</button>
      <div className={style.titulo}>
        <h1>paises del mundo</h1>
        <Buscador></Buscador>
        <Link to="/crearActividad">
          <button>crearActividad</button>
        </Link>

        <div>
          <select
            onChange={(e) => {
              setAlfa(e.target.value);
              ordenAlfabetico(e);
            }}
          >
            <option value="s">Filtro: {alfa}</option>
            <option value="asc">orden de A - Z</option>
            <option value="desc">orden de Z - A</option>
          </select>
          <select
            disabled={true}
            onChange={(e) => {
              filtrar(e.target.value);
            }}
          >
            <option disabled={true} value="sin">
              sin filtro
            </option>
            <option value="menos">menos</option>
            <option value="mas">mas</option>
          </select>

          <div>
            <select
              onChange={(e) => {
                setFilt(e.target.value);
                filtroContinente(e);
              }}
            >
              <option value="Todos">Todos</option>
              <option value="Americas">América</option>
              <option value="Africa">África</option>
              <option value="Asia">Asia</option>
              <option value="Oceania">Oceanía</option>
              <option value="Europe">Europa</option>
              <option value="Antarctic">Antártida</option>
            </select>
          </div>
        </div>
      </div>
      <Paginado
        paisesPorPag={paisesPorPag}
        pais={pais.length}
        paginado={paginado}
        paginaActual={paginaActual}
      ></Paginado>
      <div className={style.paises}>
        {paisesPagActual?.map((p, i) => (
          <Pais
            key={i}
            name={p.name}
            bandera={p.bandera}
            continente={p.continente}
            id={p.id}
          />
        ))}
      </div>
    </div>
  );
};

export default Paises;
