import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import style from "../styles/crearactiv.module.css";
import { Link } from "react-router-dom";
import { getAllCountries, postTouristActivy } from "../redux/actions";
import { organizar } from "./funciones";

const Crearactividadturist = () => {
  const dispatch = useDispatch();
  const paises = useSelector((state) => state.countries);
  const [save, setSave] = useState(1);
  const paisesParaSelec = organizar(paises);
  const temporadas = ["Verano", "Otoño", "Invierno", "Primavera"];
  const [input, setInput] = useState({
    name: "",
    dificult: "",
    duration: "",
    season: "",
    pais: [],
  });
  useEffect(() => {
    dispatch(getAllCountries());
    console.log("se ejecuto");
    console.log(input);
  }, [save]);

  function tomarValores(e) {
    e.preventDefault();
    setInput({ ...input, [e.target.name]: e.target.value });
  }

  function tomarSelec(e) {
    e.preventDefault();
    /*  if (e.target.value === "Afganistán" || "Verano") {
      if ([e.target.name] === "pais") {
        setInput({
          ...input,
          [e.target.name]: [...input.pais, "Afganistán"],
        });
        return console.log(input);
      }
      if ([e.target.name] === "season") {
        setInput({
          ...input,
          [e.target.name]: [...input.season, "Verano"],
        });
        return console.log(input);
      }
    } */
    setInput({ ...input, [e.target.name]: [...input.pais, e.target.value] });
  }

  function postear(e) {
    e.preventDefault();
    dispatch(postTouristActivy(input));
    dispatch(getAllCountries());
    document.getElementsByName("form").reset();
  }
  function limpiar() {
    setInput({
      name: "",
      dificult: "",
      duration: "",
      season: "",
      pais: [],
    });
  }

  return (
    <div className={style.divgeneral}>
      <Link to="/Home">
        <button>Volver</button>
      </Link>
      <h1>Crear una actividad turistica</h1>
      <div>
        <form onSubmit={postear}>
          <label>
            <p>Nombre</p>
          </label>
          <input
            type="text"
            value={input.name}
            name="name"
            onChange={tomarValores}
          />
          <label>
            <p>Dificultad</p>
          </label>
          <input
            type="number"
            required={true}
            min={1}
            max={5}
            value={input.dificult}
            name="dificult"
            onChange={tomarValores}
          />
          <label>
            <p>Duracion</p>
          </label>
          <input
            type="number"
            min={1}
            max={365}
            value={input.duration}
            name="duration"
            onChange={tomarValores}
          />
          <label>
            <p>Temporada</p>
          </label>
          <select name="season" onChange={tomarValores}>
            {temporadas.map((e, o) => (
              <option key={o} value={e}>
                {e}
              </option>
            ))}
          </select>
          <label>
            <p>Paises</p>
          </label>
          <select name="pais" onChange={tomarSelec}>
            {paisesParaSelec.map((e) => (
              <option key={e.id} value={e.name}>
                {e.name}
              </option>
            ))}
          </select>
          <label>
            <p>gracias</p>
            <button type="submit" onClick={(e) => setSave(save + 1)}>
              enviar
            </button>
          </label>
          <ul>
            {input.pais.map((el, i) => (
              <li key={i}>{el + " - "}</li>
            ))}
          </ul>
        </form>
      </div>
    </div>
  );
};

export default Crearactividadturist;
