import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import {
  getAllCountries,
  orderAlphabetical,
  postTouristActivy,
} from "../redux/actions";
import { organizar } from "./funciones";

const Crearactividadturist = () => {
  const dispatch = useDispatch();
  const paises = useSelector((state) => state.countries);
  const paisesParaSelec = organizar(paises);
  const temporadas = ["Verano", "Otoño", "Invierno", "Primavera"];
  const [input, setInput] = useState({
    name: "",
    dificult: "",
    duration: "",
    season: "",
  });
  useEffect(() => {
    dispatch(getAllCountries());
  }, []);

  function tomarValores(e) {
    e.preventDefault();

    setInput({ ...input, [e.target.name]: e.target.value });
  }

  function postear(e) {
    e.preventDefault();
    dispatch(postTouristActivy(input));
    console.log(input);
  }

  return (
    <div>
      <Link to="/Home">
        <button>Volver</button>
      </Link>
      <h1>Crear una actividad turistica</h1>
      <div>
        <form onSubmit={postear}>
          <label>Nombre</label>
          <input
            type="text"
            value={input.name}
            name="name"
            onChange={tomarValores}
          />
          <label>Dificultad</label>
          <input
            type="number"
            required={true}
            min={1}
            max={5}
            value={input.dificult}
            name="dificult"
            onChange={tomarValores}
          />
          <label>Duracion</label>
          <input
            type="number"
            value={input.duration}
            name="duration"
            onChange={tomarValores}
          />
          <label>Temporada</label>
          <select name="season" onChange={tomarValores}>
            {temporadas.map((e, o) => (
              <option key={o} value={e}>
                {e}
              </option>
            ))}
          </select>
          <label>Paises</label>
          <select multiple size={50}>
            {paisesParaSelec.map((e) => (
              <option key={e.id} value={e.name}>
                {e.name}
              </option>
            ))}
          </select>
          <label>
            <button type="submit">enviar</button>
          </label>
        </form>
      </div>
    </div>
  );
};

export default Crearactividadturist;
