import { GET_ALL_COUNTRIES } from "../actions/index.js";

const initialState = {
  countries: [],
  countryDetail: {},
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_ALL_COUNTRIES:
      return {
        ...state,
        countries: [
          ...action.payload,
          ...state.countries.filter((item, index) => {
            return state.countries.indexOf(item) === index;
          }),
        ],
      };
    default:
      return state;
  }
};

export default rootReducer;
